changeSet.sql:
declare
  userId number;
begin
  select identifier into userId from catissue_user where login_name like '$system';

  insert into os_scheduled_jobs
    (identifier, name, created_by, start_date, end_date, scheduled_minute, scheduled_hour, scheduled_day_of_week,
    scheduled_day_of_month, activity_status, repeat_schedule, type, command, task_impl_fqn, rt_args_provided, rt_args_help_text)
  values
    (OS_SCHEDULED_JOBS_SEQ.nextVal, 'Bulk Import Records', userId, SYSDATE, null, 10, null, null,
    null, 'Active', 'MINUTELY', 'INTERNAL', null, 'com.krishagni.catissueplus.core.importer.services.impl.ImportRecordsTask', 0, null);
end;
//

declare cursor create_ci_indices is
  select
    index_name, table_name, column_name
  from
    user_ind_columns
  where
    char_length > 0 and
    table_name not in ('CATISSUE_AUTH_PROVIDERS', 'CATISSUE_AUTH_DOMAINS', 'OS_AUTH_PROVIDERS', 'OS_AUTH_DOMAINS', 'CATISSUE_CDE') and
    index_name not in ('OS_SPEC_QTY_UNITS_UQ', 'CATISSUE_CP_PART_UNIQUE_KEY', 'USRID_TAGLABEL_UNIQUE_KEY')
  order by
    index_name;

  rec_index create_ci_indices%ROWTYPE;
  constraint_name user_constraints.constraint_name%TYPE;
begin
  open create_ci_indices;
  loop
    fetch create_ci_indices INTO rec_index;
    exit when create_ci_indices%NOTFOUND;

    begin
      select constraint_name into constraint_name FROM user_constraints where index_name = rec_index.index_name;
      dbms_output.put_line('Dropping unique/primary constraint ' ||  rec_index.index_name);
      execute immediate 'alter table ' || rec_index.table_name || ' drop constraint ' || rec_index.index_name || '';
      dbms_output.put_line('creating unique index ' || rec_index.index_name);
      execute immediate 'create unique index ' || rec_index.index_name || ' on ' || rec_index.table_name || ' (NLSSORT (' || rec_index.column_name || ', ''NLS_SORT=BINARY_CI'') )';

      exception when no_data_found
      then
        dbms_output.put_line('Dropping index ' || rec_index.index_name);
        execute immediate 'drop index '|| rec_index.index_name;
        dbms_output.put_line('Creating index ' || rec_index.index_name);
        execute IMMEDIATE 'create index ' || rec_index.index_name || ' on ' || rec_index.table_name || ' (NLSSORT (' || rec_index.column_name || ', ''NLS_SORT=BINARY_CI'') )';
    end;
  end loop;
  dbms_output.put_line('Total number of altered indices : '||create_ci_indices%ROWCOUNT);
  close create_ci_indices;
end;
//
